	import { initFocusContextMenuCallback } from '/public/behaviours/navigable/focus/focus.js';

	var transition = new Transition();

	var contextMenu = new ContextMenu([
		initFocusContextMenuCallback(transition)]);

	initHoverable();
	initSelectable();
